export function defineLetter(loan, lo) {

    var images = {
        freedmontLogo : 'data:image/png;base64,',
        equalHousingLogo : 'data:image/jpeg;base64,',
        bbbLogo : 'data:image/jpeg;base64,',
        mbaLogo : 'data:image/png;base64,',
    };

    var date = new Date();

    var letterDefinition = {

        content: [

            {
                image: images.freedmontLogo,
                fit: [209, 32.4]
            },

            {
                canvas: [{ type: 'line', x1: 0, y1: 10, x2: 500, y2: 10, lineWidth: 0.5 }]
            },

            {
                text: `${lo.addressOne} \u00b7 ${lo.addressTwo} \u00b7 ${lo.city}, ${lo.state} ${lo.zipCode}
                       Office: ${lo.officePhone}`,
                alignment: 'right',
                margin: [0, 10, 0, 0]
            },

            {
                text: 'Pre-Qualification Certificate',
                style: 'title'
            },

            {
                columns: [
                    {width: '*', text: ''},
                    {
                        width: 'auto',
                        table: {
                            body: [[
                                {
                                    text: 'Your actual rate, payment and costs could be higher.\nGet an official Loan Estimate before choosing a loan.',
                                    fontSize: 12,
                                    fillColor: '#151515',
                                    color: '#FFFFFF',
                                }
                            ]],
                        },
                    },
                    {width: '*', text: ''},
                ]
            },

            '\n',

            {
                table: {
                    body: [
                        ['Date: ', (date.getMonth() + 1) + '/' + date.getDate() + '/' + date.getFullYear()],
                        ['Applicant(s): ', loan.borrowerName],
                    ]
                },
                layout: 'noBorders',
            },

            '\n',

            {
                text: [
                    {
                        text: 'Congratulations! ',
                        bold: true,
                    },
                    'Freedmont Mortgage Funding is pleased to inform you that based upon our initial review of your credit report and the information you have provided, we have determined that this information meets program eligibility requirements for the following mortgage program:\n\n'
                ]
            },

            {
                columns: [
                    {width: '*', text: ''},
                    {
                        width: 'auto',
                        table: {
                            body: [
                                ['Loan Program: ', `${loan.program} ${loan.term} Year ${loan.rateType}`],
                                ['Sales Price: ', loan.salesPrice],
                                ['Base Loan Amount: ', loan.baseLoanAmount],
                                ['Total Loan Amount: ', loan.totalLoanAmount]
                            ]
                        }
                    },
                    {width: '*', text: ''},
                ]
            },

            '\nSubmission of loan documents was not required. However, you may voluntarily submit documents so we can give your request a more thorough review. Your loan is not locked. A formal mortgage commitment will be issued after receipt of a complete application package and review of the following information:\n\n',

            {
                ul: [
                    'A fully executed sales agreement for an acceptable property.',
                    'A satisfactory appraisal of subject property.',
                    'Acceptable verification of employment, and assets in accordance with the underwriting guidelines for the mortgage program referenced above.',
                ]
            },

            '\n',

            'We welcome the opportunity to be of service to you. Should you have any questions or concerns, please feel free to contact me at ' + lo.cellPhone + '\n',

            {
                text: `\nSincerely,\n\n${lo.firstName} ${lo.lastName}\nNMLS#: ${lo.nmlsNumber}\nEmail: ${lo.email}\nWebsite: www.freedmont.com`
            },

            {
                columns: [
                    {
                        width: '*',
                        image: images.bbbLogo,
                        fit: [89, 39]
                    },
                    {
                        width: '*',
                        image: images.mbaLogo,
                        fit: [59, 30],
                        style: 'centerimg'
                    },
                    {
                        width: '*',
                        image: images.equalHousingLogo,
                        fit: [29, 31],
                        style: 'rightimg'
                    },
                ]
            },

            {
                text: 'Allied Mortgage Group Inc. dba Freedmont Mortgage Funding (NMLS #1067) corporate office is located at 7 Bala Ave #108 Bala Cynwyd, Pa 19004 (610) 668-2745.  Full licensing is found at www.nmlsconsumer.org. This request was not reviewed by an underwriter and is not a commitment to lend. All loans are subject to underwriting guidelines and are subject to change without notice.   Allied Mortgage Group is not affiliated with any government agency.',
                style: 'alliedDisclaimer',
            },

        ],

        styles: {
            rightimg: {
                alignment: 'right',
                margin: [0, 4.5, 0, 0],
            },
            centerimg: {
                alignment: 'center',
                margin: [0, 9, 0, 0],
            },
            title: {
                alignment: 'center',
                fontSize: 24,
                margin: [0, 10, 0, 0],
            },
            alliedDisclaimer: {
                italic: true,
                fontSize: 9,
                alignment: 'center',
                margin: [0, 5, 0, 0],
            },
        },

        defaultStyle: {
            fontSize: 12,
        },
        pageSize: 'LETTER',
        pageMargins: [58, 22, 58, 33],

    };

    return letterDefinition;

}
